CREATE DATABASE db_Marketplace2USE db_Marketplace2CREATE TABLE tb_Usuarios (	Id INT PRIMARY KEY IDENTITY NOT NULL,	Nome VARCHAR (50) NOT NULL,	Email VARCHAR (50) NOT NULL,	Senha VARCHAR(100) NOT NULL,	Endereco VARCHAR (100) NULL,)CREATE TABLE tb_Categorias (	Id INT PRIMARY KEY IDENTITY NOT NULL,	Descricaoo VARCHAR (50))CREATE TABLE tb_Produtos (	Id INT PRIMARY KEY IDENTITY NOT NULL,	NomeProduto VARCHAR (50) NOT NULL,	Descricao VARCHAR (150) NOT NULL,	Preco FLOAT NULL,	FK_Criador INT NOT NULL,	FK_Categoria INT NOT NULL,	FOREIGN KEY (FK_Criador) REFERENCES Usuarios (Id),	FOREIGN KEY (FK_Categoria) REFERENCES Categorias (Id))CREATE TABLE tb_Pedidos(	PrevisaoEntrega VARCHAR(50) NULL,	FK_Comprador INT NOT NULL,	FK_Produto INT NOT NULL,	FOREIGN KEY (FK_Comprador) REFERENCES Usuarios (Id),	FOREIGN KEY (FK_Produto) REFERENCES Produtos (Id))
